import "./App.css";
import React, {useState} from "react";

export function Component() {

  let [word, setWord] = useState('')

  return (
    <>
      <main>
        <img src="/drawers.jpg" alt="Foto" className="drawer"/>
        <div className="flex-behave">
          <article className="component-info">
            <h3>Shift the overall look and feel by adding these wonderful touches to furniture in your home</h3>
            <p>Ever been in a room and felt like something was missing? Perhaps it felt slightly bare and uninviting. I've got some simple tips to help you make any room feel complete.</p>
          </article>

          <article className="user-info">
            <div className="user-contact-route">
              <div className="user-and-date">
                <img src="/avatar-michelle.jpg" />
                <div className="date">
                  <span>Michelle Appleton</span>
                  <p>28 Jun 2020</p>
                </div>
              </div>

              <img src="/icon-share.svg" className="share" />
            </div>

            <div className="contacts-mobile" style={{opacity: word}}>
              <div className="icons">
                <p>SHARE</p>
                <img src="/icon-facebook.svg" />
                <img src="/icon-twitter.svg" />
                <img src="/icon-pinterest.svg" />
              </div>

              <img src="/icon-share.svg" className="share1" onClick={() => {
                if (word == '0') {
                  setWord(word = '1')
                } else {
                  setWord(word = '0')
                }
              }}/>
            </div>

            <div className="contacts-mobile" style={{opacity: word}}>
              <div className="icons">
                <p>SHARE</p>
                <img src="/icon-facebook.svg" />
                <img src="/icon-twitter.svg" />
                <img src="/icon-pinterest.svg" />
              </div>

              <img src="/icon-share.svg" className="share1" onClick={() => {
                if (word == '0') {
                  setWord(word = '1')
                } else {
                  setWord(word = '0')
                }
              }}/>
            </div>
          </article>
        </div>

      </main>
    </>
  );
}
